<?php

declare(strict_types=1);

use api\Entities\Actor;
use api\Data\ActorDAO;
use PHPUnit\Framework\TestCase;

class ActorDAOTest extends TestCase
{
  private $data;
  private $actorObject;
  private $actorDAO;
  private $allObjects;

  public function setUp(): void
  {
    $this->data = ['id' => 1, 'firstName' => 'Steve', 'lastName' => 'Buscemi', 'image' => '<img alt="Steve Buscemi" src="data:image/jpeg;base64,"/>'];
    $this->actorObject = new Actor($this->data['id'], $this->data['firstName'], $this->data['lastName'], $this->data['image']);
    $this->actorDAO = new ActorDAO();

    $this->allObjects = [$this->actorObject, new Actor(2, 'Jason', 'Isaacs', '<img alt="Jason Isaacs" src="~/img/placeholderPerson.jpg">'), new Actor(3, 'Bruce', 'Willis', '<img alt="Bruce Willis" src="~/img/placeholderPerson.jpg">')];

    $this->actorDAO->startTransaction();
  }

  public function tearDown(): void
  {
    $this->actorDAO->rollbackTransaction();
  }

  public function test_getById()
  {
    $this->assertEquals($this->actorObject, $this->actorDAO->getById(1));
  }

  public function test_getByName()
  {
    $this->assertEquals($this->actorObject, $this->actorDAO->getByName($this->data['firstName'], $this->data['lastName']));
  }

  public function test_getAll()
  {
    $this->assertEquals($this->allObjects, $this->actorDAO->getAll());
  }

  public function test_createNewActor()
  {
    $this->actorDAO->createNewActor('Florence', 'Pugh', null);
    $this->assertEquals('Florence', $this->actorDAO->getByName('Florence', 'Pugh')->getFirstName());
  }

  public function test_updateActor()
  {
    $this->actorDAO->updateActor(1, 'Steven', null, null);
    $this->assertEquals('Steven', $this->actorDAO->getById(1)->getFirstName());
    $this->actorDAO->updateActor(1, null, 'Buscami', null);
    $this->assertEquals('Buscami', $this->actorDAO->getById(1)->getLastName());
  }

  public function test_removeActor()
  {
    $this->actorDAO->removeActor(1);
    $this->assertEquals(null, $this->actorDAO->getById(1));
  }
}
